import React from 'react'
import styled from 'styled-components'
import Arrow from '../Images/arrow.png';
import American from '../Images/American.png';
import {connect} from "react-redux";

function FlightCard(props) {
  return (
        <Card theme={props.theme.theme}>
            <Airport>
                <Row center>
                {props.hotel.name && props.hotel.name && <AirPortCode>{props.hotel.name}</AirPortCode>}
                </Row>
                <Row>
                    <p>Guest Rating</p>
                    {props.hotel.overallGuestRating && props.hotel.overallGuestRating && <AirPortCode>{props.hotel.overallGuestRating}</AirPortCode>}
                    <p>Number of Reviews</p>
                    {props.hotel.totalReviewCount && props.hotel.totalReviewCount && <AirPortCode>{props.hotel.totalReviewCount}</AirPortCode>}
                    <p>Star Rating</p>
                    {props.hotel.starRating && props.hotel.starRating && <AirPortCode>{props.hotel.starRating}</AirPortCode>}
                    <p>Features</p>
                    {props.hotel.keyFeatures && props.hotel.keyFeatures && <AirPortCode>{props.hotel.keyFeatures}</AirPortCode>}

                </Row>

            </Airport>


        </Card>
  )
}

const Card = styled.div`
    width: 900px;
    border: 3px solid ${({ theme }) => theme.cardBorder};
    margin-bottom: 25px;
    background-color: ${({ theme }) => theme.body};
    border-radius: 4px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const FlightDuration = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 25px 0;

    img {
        margin: 10px 0;
    }

    p {
        margin: 0;
    }
`;

const Airport = styled.div`
    flex: 1 0;
    display: flex;
    flex-direction: column;
    margin: 0 20px;
`;

const Row = styled.div`
    margin: 10px 0;
    display: flex;
    flex-direction: column;

    margin: ${props => props.center && '10px auto'};

    align-items: ${props => props.end ? 'flex-end' : 'flex-start'};

    p {
        margin: 0;
    }

`;

const AirPortCode = styled.p`
    font-size: 24px;
`;

const FlightTime = styled.p`
    font-size: 30px;
`;

const mapStateToProps = (state) => {
    return state;
  }

  export default connect(mapStateToProps)(FlightCard);
