import React from 'react'
import styled from 'styled-components';
import Arrow from '../Images/arrow.png';
import American from '../Images/American.png';
import {connect} from "react-redux";

function HotelCard(props) {
  return (
    <Card theme={props.theme.theme}>
        <Airport>
            <Row center>
                <AirPortCode>DFW</AirPortCode>
            </Row>
            <Row>
                <p>Departs</p>
                <FlightTime>8:05 AM</FlightTime>
            </Row>
            <Row>
                <p>Gate: 19</p>
                <p>Terminal: C</p>
            </Row>
        </Airport>
        <FlightDuration>
            <p>April 29th, 2022</p>
            <img src={Arrow} />
            <p style={{fontSize: '24px'}}>1h 25m</p>
            <p>American Airlines</p>
            <img style={{width: '40px', height: '40px', marginBottom: '0'}} src={American} />
        </FlightDuration>

        <Airport>
            <Row center>
                <AirPortCode>LAX</AirPortCode>
            </Row>
            <Row end>
                <p>Arrives</p>
                <FlightTime>12:05 PM</FlightTime>
            </Row>
            <Row end>
                <p>Gate: 24</p>
                <p>Terminal: A</p>
            </Row>
        </Airport>
    </Card>
  )
}

const Card = styled.div`
    width: 900px;
    border: 3px solid ${({ theme }) => theme.cardBorder};
    margin-bottom: 25px;
    background-color: ${({ theme }) => theme.body};
    border-radius: 4px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const FlightDuration = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 25px 0;

    img {
        margin: 10px 0;
    }

    p {
        margin: 0;
    }
`;

const Airport = styled.div`
    flex: 1 0;
    display: flex;
    flex-direction: column;
    margin: 0 20px;
`;

const Row = styled.div`
    margin: 10px 0;
    display: flex;
    flex-direction: column;

    margin: ${props => props.center && '10px auto'};

    align-items: ${props => props.end ? 'flex-end' : 'flex-start'};

    p {
        margin: 0;
    }

`;

const AirPortCode = styled.p`
    font-size: 24px;
`;

const FlightTime = styled.p`
    font-size: 30px;
`;

const mapStateToProps = (state) => {
    return state;
  }

  export default connect(mapStateToProps)(HotelCard);
