import React from 'react'
import styled from 'styled-components';
import France from '../Images/france.jpg';
import {connect} from "react-redux";

function HotelCard(props) {
  return (
    <Card theme={props.theme.theme}>
        <Column>
            <img src={France} />
        </Column>
        <Column>
            <Row>
                <HotelName>Hotel Paradise</HotelName>
            </Row>
            <Row>
                <Location>Paris, France</Location>
            </Row>
            <Row>
                {/* <Ammenties>Breakfast</Ammenties>
                <Ammenties>AirPort Shuttle</Ammenties> */}
            </Row>
        </Column>
    </Card>
  )
}

const Card = styled.div`
    width: 900px;
    border: 3px solid ${({ theme }) => theme.cardBorder};
    margin-bottom: 25px;
    background-color: ${({ theme }) => theme.body};
    border-radius: 4px;
    display: flex;
`;

const Column = styled.div`
    flex: 1 0;

    img {
        width: 100%;
        height: 100%;
    }
`;

const Row = styled.div`
    margin-left: 10px;
    text-align: left;
`;

const HotelName = styled.p`
    font-size: 24px;
`;

const Location = styled.p`
    font-size: 16px;
`;

const mapStateToProps = (state) => {
    return state;
  }

  export default connect(mapStateToProps)(HotelCard);
