import React from 'react'
import styled from 'styled-components'
import Arrow from '../Images/arrow.png';
import American from '../Images/American.png';
import {connect} from "react-redux";
import Moment from "react-moment";

function FlightCard(props) {

    let durationString
    let day
    let hour
    let min
    let duration = props.flight.slice_data.slice_0.flight_data.flight_0.info.duration
    duration = duration.split(":")
    if (duration[0] === "00" && duration[1] === "00") {
        durationString = `${parseInt(duration[2])}m`
    } else if (duration[0] === "00") {
        durationString = `${parseInt(duration[1])}h ${parseInt(duration[2])}m`
    } else {
        durationString = `${parseInt(duration[0])}d ${parseInt(duration[1])}h ${parseInt(duration[2])}m`
    }


  return (
        <Card theme={props.theme.theme}>
            <Airport>
                <Row center>
                    {props.flight.slice_data.slice_0.departure && props.flight.slice_data.slice_0.departure.airport && <AirPortCode>{props.flight.slice_data.slice_0.departure.airport.code}</AirPortCode>}
                </Row>
                <Row>
                    <p>Departs</p>
                    {props.flight.slice_data.slice_0.flight_data.flight_0.departure && props.flight.slice_data.slice_0.flight_data.flight_0.departure.datetime && <FlightTime>{props.flight.slice_data.slice_0.flight_data.flight_0.departure.datetime.time_12h}</FlightTime>}
                </Row>
                <Row>
                    <p>Gate: 19</p>
                    <p>Terminal: C</p>
                </Row>
            </Airport>
            <FlightDuration>
                {props.flight.slice_data.slice_0.departure && props.flight.slice_data.slice_0.departure.datetime && <AirPortCode><Moment format='LL'>{props.flight.slice_data.slice_0.departure.datetime.date}</Moment ></AirPortCode>}

                <img src={Arrow} />
                {props.flight.slice_data.slice_0.flight_data.flight_0 && props.flight.slice_data.slice_0.flight_data.flight_0.info && <p style={{fontSize: '24px'}}>{durationString}</p>}
                {props.flight.slice_data.slice_0 && props.flight.slice_data.slice_0.airline && <p>{props.flight.slice_data.slice_0.airline.name}</p>}
                <img style={{width: '40px', height: '40px', marginBottom: '0'}} src={props.flight.slice_data.slice_0.airline.logo} />
            </FlightDuration>

            <Airport>
                <Row center>
                    {props.flight.slice_data.slice_0.arrival && props.flight.slice_data.slice_0.arrival.airport && <AirPortCode>{props.flight.slice_data.slice_0.arrival.airport.code}</AirPortCode>}
                </Row>
                <Row end>
                    <p>Arrives</p>
                    {props.flight.slice_data.slice_0.flight_data.flight_0.arrival && props.flight.slice_data.slice_0.flight_data.flight_0.arrival.datetime && <FlightTime>{props.flight.slice_data.slice_0.flight_data.flight_0.arrival.datetime.time_12h}</FlightTime>}
                </Row>
                <Row end>
                    <p>Gate: 24</p>
                    <p>Terminal: A</p>
                </Row>
            </Airport>
        </Card>
  )
}

const Card = styled.div`
    width: 900px;
    border: 3px solid ${({ theme }) => theme.cardBorder};
    margin-bottom: 25px;
    background-color: ${({ theme }) => theme.body};
    border-radius: 4px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const FlightDuration = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 25px 0;

    img {
        margin: 10px 0;
    }

    p {
        margin: 0;
    }
`;

const Airport = styled.div`
    flex: 1 0;
    display: flex;
    flex-direction: column;
    margin: 0 20px;
`;

const Row = styled.div`
    margin: 10px 0;
    display: flex;
    flex-direction: column;

    margin: ${props => props.center && '10px auto'};

    align-items: ${props => props.end ? 'flex-end' : 'flex-start'};

    p {
        margin: 0;
    }

`;

const AirPortCode = styled.p`
    font-size: 24px;
`;

const FlightTime = styled.p`
    font-size: 30px;
`;

const mapStateToProps = (state) => {
    return state;
  }

  export default connect(mapStateToProps)(FlightCard);
